type CorrectElectionConstants struct {
	Q schema.BigInt
	P schema.BigInt
	C schema.BigInt
	G schema.BigInt
}

func MakeCorrectElectionConstants() CorrectElectionConstants {
	return CorrectElectionConstants{
		Q: initializeBigIntWithString(QHexString),
		P: initializeBigIntWithString(PHexString),
		C: initializeBigIntWithString(RHexString),
		G: initializeBigIntWithString(GHexString),
	}
}

func initializeBigIntWithString(init string) schema.BigInt {
	bigInt := schema.BigInt{}
	bigInt.Int.SetString(init, 16)

	return bigInt
}
